from typing import Listfrom fastapi import APIRouter, HTTPExceptionfrom backend.app.dependencies.database import SessionDepfrom backend.app.models.groups import Group as GroupORMfrom backend.app.schemas.groups import Groupfrom urllib.parse import unquoterouter = APIRouter(tags=['Группы'], prefix='/groups')@router.get('/all')def get_all_groups(session:SessionDep):    res = session.query(GroupORM).all()    print(res)    return res@router.get('/', response_model=List[Group])def get_all_groups_in(session:SessionDep, ids: str):    ids = [int(i) for i in unquote(ids).split(',')]    return session.query(GroupORM).filter(GroupORM.id.in_(ids)).all()@router.get('/{group_id}', response_model=Group)def get_group_by_id(group_id: int, session: SessionDep):    res = session.get(GroupORM, group_id)    if not res:        raise HTTPException(status_code=404, detail="Группы с таким id нет!")    return res@router.get('/{curse}/{group_number}')def get_group_id(curse:int, group_number: int, session:SessionDep):    res = session.query(GroupORM.id).where(GroupORM.curse == curse).where(GroupORM.number == group_number).first()    if res:        return {'group_id': res[0]}