from fastapi import APIRouter, HTTPExceptionfrom backend.app.dependencies.database import SessionDepfrom backend.app.models.groups import Group as GroupORMfrom backend.app.schemas.groups import Grouprouter = APIRouter(tags=['groups'], prefix='/groups')@router.get('/{group_id}', response_model=Group)def get_group_by_id(group_id: int, session: SessionDep):    res = session.get(GroupORM, group_id)    if not res:        raise HTTPException(status_code=404, detail="Группы с таким id нет!")    return res