from fastapi import APIRouter, HTTPExceptionfrom backend.app.dependencies.database import SessionDepfrom backend.app.models.students import Student as StudentORMfrom backend.app.schemas.students import Studentrouter = APIRouter(tags=['Студенты'], prefix='/students')@router.get('/{stud_id}', response_model=Student)def get_stud_by_id(stud_id:int, session: SessionDep):    res = session.get(StudentORM, stud_id)    if not res:        raise HTTPException(status_code=404, detail="Студента с таким id нет!")    return res