from fastapi import APIRouterfrom typing import Listfrom sqlalchemy import funcfrom backend.app.schemas.subjects import Subjectfrom backend.app.models.subjects import Subject as SubjectORMfrom backend.app.dependencies.database import SessionDepfrom urllib.parse import unquoterouter = APIRouter(tags=['subjects'], prefix='/subjects')@router.get('/', response_model=List[Subject])def get_subjects(sub_name:str, session: SessionDep):    decoded_sub_name = unquote(sub_name)    subjects = session.query(SubjectORM).filter(func.lower(SubjectORM.sub_name).like(decoded_sub_name.lower() + '%'))    return subjects