from fastapi import APIRouter, HTTPExceptionfrom backend.app.dependencies.database import SessionDepfrom backend.app.schemas.subjects import Subjectfrom typing import Listfrom backend.app.crud import get_subjects, get_subjects_in, get_one_sub_id_by_sub_name, get_subjects_first_lettersrouter = APIRouter(tags=['Предметы'], prefix='/subjects')@router.get('/all', response_model=List[Subject])def get_all_subjects(session:SessionDep):    subjects = get_subjects(session)    return subjects@router.get('/', response_model=List[Subject])def get_all_subjects_in(session:SessionDep, ids: str):    subjects = get_subjects_in(session, ids)    return subjects@router.get('/first_letters', response_model=List[Subject])def get_subjects_first_letters(sub_name:str, session: SessionDep):    subjects = get_subjects_first_letters(session, sub_name)    return subjects@router.get('/sub_id')def get_sub_id_by_sub_name(sub_name:str, session: SessionDep):    subject_id = get_one_sub_id_by_sub_name(session, sub_name)    if not subject_id:        raise HTTPException(status_code=404, detail='Нет предмета с таким названием!')    return subject_id