from fastapi import APIRouterfrom backend.app.dependencies.database import SessionDepfrom backend.app.schemas.subjects_semester import SubjectSemesterCreate, SubjectSemesterfrom typing import Listfrom backend.app.crud import get_subjects_semester, get_sub_sem_syllabuses_in, insert_new_sub_semester, get_subjects_sem_by_first_letters_sub, delete_sub_semrouter = APIRouter(tags=['Предметы-семестр'], prefix='/subjects_semester')@router.get('/all', response_model=List[SubjectSemester])def get_all_subjects_semester(session:SessionDep):    subjects_semester = get_subjects_semester(session)    return subjects_semester@router.get('/', response_model=List[SubjectSemester])def get_all_syllabuses_in(session:SessionDep, ids: str):    subjects_semester = get_sub_sem_syllabuses_in(session, ids)    return subjects_semester@router.post('/')def insert_sub_semester(sub_semester:SubjectSemesterCreate, session: SessionDep):    return insert_new_sub_semester(session, sub_semester)@router.get('/first_letters')def get_one_sub_sem_by_first_letters_sub(sub_name:str, session:SessionDep):    subjects_semester = get_subjects_sem_by_first_letters_sub(session, sub_name)    return subjects_semester@router.delete('/{sub_sem_id}')def delete_sub_sem_(session:SessionDep, sub_sem_id:int):    return delete_sub_sem(session, sub_sem_id)