from fastapi import APIRouter, Bodyfrom backend.app.dependencies.database import SessionDepfrom backend.app.models.teachers_subjects_semester import TeacherSubjectSemester as tssORMfrom backend.app.schemas.teachers_subjects_semester import TeacherSubjectSemesterfrom typing import Listrouter = APIRouter(tags=['Преподаватели-предметы-семестр'], prefix='/teachers_subjects_semester')@router.get("/all", response_model=List[TeacherSubjectSemester])def get_all_teachers_subs_semester(session: SessionDep):    res = session.query(tssORM).all()    if res:        return res@router.patch('/{tss_id}')def update_teacher_id_in_tss(tss_id:int, session:SessionDep , new_teacher_id: int = Body(..., embed=True)):    tss_obj = session.get(tssORM, tss_id)    tss_obj.teacher_id = new_teacher_id    session.commit()    return {"OK": 200}