from database import enginefrom sqlalchemy import Enginefrom sqlalchemy_utils import database_exists, create_database, drop_databasefrom backend.app.models.base import Basedef create_tables(engine:Engine, baseClass):    if database_exists(engine.url):        drop_database(engine.url)        create_database(engine.url)        baseClass.metadata.create_all(engine)    else:        create_database(engine.url)        baseClass.metadata.create_all(engine)create_tables(engine, Base)