from database import enginefrom sqlalchemy import Enginefrom sqlalchemy_utils import database_exists, create_database, drop_databasefrom backend.app.models.base import Basefrom backend.app.models.students import Studentfrom backend.app.models.groups import Groupfrom backend.app.models.subjects import Subjectfrom backend.app.models.syllabuses import Syllabusfrom backend.app.models.subjects_semester import SubjectSemesterfrom backend.app.models.teachers import Teacherfrom backend.app.models.teachers_subjects_semester import TeacherSubjectSemesterdef create_tables(engine:Engine, baseClass):    if database_exists(engine.url):        drop_database(engine.url)        create_database(engine.url)        baseClass.metadata.create_all(engine)    else:        create_database(engine.url)        baseClass.metadata.create_all(engine)create_tables(engine, Base)