from sqlalchemy import create_engineimport osfrom sqlalchemy.orm import sessionmakerfrom dotenv import load_dotenvload_dotenv()DATABASE_URL = (f'{os.getenv("DB_DRIVER")}://{os.getenv("DB_USER")}'                f':{os.getenv("DB_PASSWORD")}@{os.getenv("DB_HOST")}'                f':{os.getenv("DB_PORT")}/{os.getenv("DB_NAME")}')engine = create_engine(DATABASE_URL, echo=True)new_session = sessionmaker(bind=engine)def get_session():    with new_session() as session:        yield session