from backend.app.database import new_sessionfrom backend.app.models import Groupfrom backend.app.models.teachers import Teacherfrom backend.app.models.subjects import Subjectdef insert_teachers():    with open('data/teachers.csv', 'r') as file:        teachers = file.read().split('\n')    with new_session() as session:        for teacher in teachers:            params = dict(zip(['teacher_surname', 'teacher_name', 'teacher_father_name', 'teacher_position', 'department'], teacher.split(',')))            t = Teacher(**params)            session.add(t)            session.flush()        session.commit()def insert_subjects():    with open('data/subjects.txt', 'r') as file:        subjects = file.read().split('\n')    with new_session() as session:        for sub in subjects:            s = Subject(sub_name=sub)            session.add(s)            session.flush()        session.commit()def insert_groups():    with new_session() as session:        for curse in range(1, 5):            for number, spec in zip(range(1, 5), ['mech', 'web', 'ped', 'km']):                g = Group(curse=curse, number=number, speciality=spec)                session.add(g)                session.flush()        session.commit()insert_teachers()insert_subjects()insert_groups()