from typing import Literal, Listfrom sqlalchemy import Integerfrom sqlalchemy.dialects.postgresql import ENUMfrom .base import Basefrom sqlalchemy.orm import Mapped, mapped_column, relationshipclass Group(Base):    __tablename__ = 'Groups'    id: Mapped[int] = mapped_column('id', primary_key=True)    curse: Mapped[int] = mapped_column('curse', Integer, nullable=False)    number: Mapped[int] = mapped_column('number', Integer, nullable=False)    speciality: Mapped[Literal['km', 'ped', 'web', 'mech', 'km_mech']] = mapped_column('speciality', ENUM('km', 'ped', 'web', 'mech', 'km_mech'))    studs: Mapped[List['Student']] = relationship(back_populates='group')    # messages --> advanced feature to send messages to each group