from .base import Basefrom sqlalchemy.orm import mapped_column, Mapped, relationshipfrom sqlalchemy import String, Integerfrom typing import Listclass Subject(Base):    __tablename__ = 'Subjects'    id: Mapped[int] = mapped_column('id', Integer, primary_key=True, autoincrement=True)    sub_name: Mapped[str] = mapped_column('sub_name', String(100), nullable=False)    subjects_semester: Mapped[List['SubjectSemester']] = relationship('SubjectSemester', back_populates='sub')    def __repr__(self) -> str:        return f"<Subject(id='{self.id}', sub_name='{self.sub_name}')>"