from .base import Basefrom sqlalchemy import Integerfrom sqlalchemy.orm import mapped_column, Mapped, relationshipfrom sqlalchemy.dialects.postgresql import ENUMfrom typing import Literal, Listclass Syllabus(Base):    __tablename__ = 'Syllabuses'    id: Mapped[int] = mapped_column('id', primary_key=True)    speciality: Mapped[Literal['km', 'ped', 'web', 'mech', 'km_mech']] = mapped_column('speciality', ENUM('km', 'ped', 'web', 'mech', 'km_mech', name='specialities'), nullable=False)    start_year: Mapped[int] = mapped_column('start_year', Integer, nullable=False)    subjects_semester: Mapped[List['SubjectSemester']] = relationship('SubjectSemester', back_populates='syllabus')    def __repr__(self) ->str:        return f"<Syllabus(id='{self.id}', speciality='{self.speciality}', start_year='{self.start_year}')>"