from typing import Literalfrom sqlalchemy import Integer, String, Float, ForeignKey, Booleanfrom sqlalchemy.dialects.postgresql import ENUMfrom .base import Basefrom sqlalchemy.orm import Mapped, mapped_column, relationshipclass Teachers(Base):    __tablename__ = 'Teachers'    id: Mapped[int] = mapped_column('id', primary_key=True)    teacher_surname: Mapped[str] = mapped_column('teacher_surname', String(50), nullable=False)    teacher_name: Mapped[str] = mapped_column('teacher_name', String(50), nullable=False)    teacher_father_name: Mapped[str] = mapped_column('teacher_father_name', String(50), nullable=False)    department: Mapped[str] = mapped_column('department', String(200), nullable=False)    teacher_position: Mapped[str] = mapped_column('teacher_position', String(200), nullable=False)