from sqlalchemy import Stringfrom .base import Basefrom sqlalchemy.orm import Mapped, mapped_columnfrom sqlalchemy.dialects.postgresql import ENUMclass User(Base):    __tablename__ = 'Users'    id: Mapped[int] = mapped_column('id', primary_key=True, unique=True)    login: Mapped[str] = mapped_column('login', String(70), unique=True)    password: Mapped[str] = mapped_column('password', String(70), unique=True)    role: Mapped[str] = mapped_column('role', ENUM('stud', 'teacher', 'admin', name='role'))    def __repr__(self):        return f"<User(id={self.id}, login={self.login}, password={self.password}, role={self.role})>"