from pydantic import BaseModel, Fieldfrom typing import Literal, Unionclass Attendance(BaseModel):    id: int    stud_id: int    class_id: int    is_attended: Union[None, Literal['У', 'Н', 'П', 'В']] = Field(default=None)    mark: int = Field(default=None)    teacher_comment: str = Field(default=None)    model_config = {        "json_schema_extra": {            "examples": [                {                    "id":5,                    "stud_id": 4,                    "class_id": 5,                    "is_attended": "П",                    "mark": 2,                    "teacher_comment": "Не знает условие Неймана!"                }            ]        }    }