from datetime import datefrom pydantic import BaseModelclass ClassCreate(BaseModel):    class_date: date    tss_id: intclass Class(ClassCreate):    id: int    model_config = {        "json_schema_extra": {            "examples": [                {                    "id":4,                    "class_date": "2025-04-01",                    "tss_id": 5                }            ]        }    }