from datetime import datefrom pydantic import BaseModelclass Class(BaseModel):    id: int    class_date: date    one_class_id: int    model_config = {        "json_schema_extra": {            "examples": [                {                    "id":4,                    "class_date": "2025-04-01",                    "one_class_id": 5                }            ]        }    }