from pydantic import BaseModelfrom datetime import dateclass Exam(BaseModel):    id: int    teacher_subject_semester_id: int    exam_date: date    model_config = {        "json_schema_extra": {            "examples": [                {                    "id":5,                    "teacher_subject_semester_id": 4,                    "exam_date": "2025-06-03",                }            ]        }    }