from pydantic import BaseModel, Fieldclass ExamSession(BaseModel):    id: int    stud_id: int    exam_id: int    semester_mark: int = Field(ge=0, le=10)    exam_mark: int = Field(ge=0, le=10)    final_mark: int = Field(ge=0, le=10)    model_config = {        "json_schema_extra": {            "examples": [                {                    "id":5,                    "stud_id": 4,                    "exam_id": 5,                    "semester_mark": 8,                    "exam_mark": 9,                    "final_mark": 9                }            ]        }    }