from pydantic import BaseModel, Fieldfrom typing import Literalclass Group(BaseModel):    id: int    curse: int = Field(ge=1, le=4)    number: int    speciality: Literal['km', 'ped', 'web', 'mech', 'km_mech']    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 1,                    "curse": 3,                    "number": 5,                    "speciality": "km",                }            ]        }    }