from pydantic import BaseModel, Fieldfrom datetime import timeclass Schedule(BaseModel):    id: int    n_week: int = Field(ge=1, le=2)    week_day: int = Field(ge=1, le=7)    n_class: int = Field(ge=1, le=5)    start_time: time    end_time: time    teacher_sub_semester_id: int    class_room: int    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 1,                    "n_week": 1,                    "week_day": 2,                    "n_class": 1,                    "start_time": "8:15",                    "end_time": "9:35",                    "teacher_sub_semester_id": 2,                    "class_room": 403                }            ]        }    }