from pydantic import BaseModelfrom pydantic import Fieldfrom typing import Literal, Unionclass Student(BaseModel):    id: int = Field(gt=0)    stud_name: str = Field(max_length=40)    stud_father_name: str = Field(max_length=40)    stud_surname: str = Field(max_length=40)    total_avg_score: Union[None, float] = Field(ge=0, le=10)    last_avg_score: Union[None, float] = Field(ge=0, le=10)    group_id: int    sub_group: Literal['a', 'b', 'c']    study_form: Literal['paid', 'free']    is_active: bool    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 1,                    "stud_name": "Иван",                    "stud_father_name": "Петрович",                    "stud_surname": "Иванов",                    "total_avg_score": 7.4,                    "last_avg_score": 8.0,                    "group_id": 4,                    "sub_group": "a",                    "study_form": "free",                    "is_active": True                }            ]        }    }