from pydantic import BaseModel, Field, ConfigDictclass Subject(BaseModel):    id: int    sub_name: str = Field(max_length=100)    model_config = ConfigDict(from_attributes=True,        json_schema_extra = {            "examples": [                {                    "id": 5,                    "sub_name": "Базы Данных"                }            ]        }                              )