from pydantic import BaseModel, ConfigDict, Fieldclass SubjectSemesterCreate(BaseModel):    sub_id: int    semester: int = Field(ge=1, le=7)    has_exam: bool    has_zachet: bool    has_sub_group: bool    lecture_per_week: float = Field(ge=0, le=3)    practice_per_week: float = Field(ge=0, le=3)    plan_id: intclass SubjectSemester(SubjectSemesterCreate):    id: int    model_config = ConfigDict(from_attributes=True,        json_schema_extra={            "examples": [                {                    "id": 3,                    "sub_id": 3,                    "semester": 1,                    "has_exam": True,                    "has_zachet": False,                    "has_sub_group": False,                    "lecture_per_week": 1.0,                    "practice_per_week": 0.5,                    "plan_id": 3                }            ]    }    )