from pydantic import BaseModelfrom pydantic import Fieldclass SubjectSemester(BaseModel):    id: int    sub_id: int    group_id: int    semester: int = Field(ge=1, le=2)    has_exam: bool    has_zachet: bool    has_sub_group: bool    lecture_per_week: float    practice_per_week: float    plan_id: int    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 3,                    "sub_id": 3,                    "group_id": 5,                    "semester": 1,                    "has_exam": True,                    "has_zachet": False,                    "has_sub_group": False,                    "lecture_per_week": 1.0,                    "practice_per_week": 0.5,                    "plan_id": 3                }            ]        }    }