from typing import Literalfrom pydantic import BaseModelfrom pydantic import Fieldclass SubjectSemesterCreate(BaseModel):    sub_id: int    semester: int = Field(ge=1, le=7)    has_exam: bool    has_zachet: bool    has_sub_group: bool    lecture_per_week: float = Field(ge=0, le=3)    practice_per_week: float = Field(ge=0, le=3)    plan_id: intclass SubjectSemester(SubjectSemesterCreate):    id: int    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 3,                    "sub_id": 3,                    "semester": 1,                    "has_exam": True,                    "has_zachet": False,                    "has_sub_group": False,                    "lecture_per_week": 1.0,                    "practice_per_week": 0.5,                    "plan_id": 3                }            ]        }    }class SubjectSemesterFull(BaseModel):    id: int    sub_name: str    semester: int = Field(ge=1, le=7)    has_exam: bool    has_zachet: bool    has_sub_group: bool    lecture_per_week: float = Field(ge=0, le=3)    practice_per_week: float = Field(ge=0, le=3)    speciality: Literal['km', 'web', 'ped', 'mob', 'mech', 'km_mech']    start_year: int    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 3,                    "sub_name": "Базы данных",                    "semester": 1,                    "has_exam": True,                    "has_zachet": False,                    "has_sub_group": False,                    "lecture_per_week": 1.0,                    "practice_per_week": 0.5,                    "speciality": 'km',                    "start_year": 2023                }            ]        }    }