from pydantic import BaseModel, ConfigDictfrom typing import Literalclass Syllabus(BaseModel):    id: int    speciality: Literal['km', 'ped', 'web', 'mech', 'km_mech']    start_year: int    model_config = ConfigDict(from_attributes=True,        json_schema_extra={            "examples": [                {                    "id": 3,                    "speciality": 'web',                    "start_year": 2020,                }            ]        }                              )