from pydantic import BaseModelfrom pydantic import Fieldclass Teacher(BaseModel):    id: int = Field(gt=0)    teacher_surname: str = Field(max_length=50)    teacher_name: str = Field(max_length=50)    teacher_father_name: str = Field(max_length=50)    department: str = Field(max_length=200)    teacher_position: str = Field(max_length=150)    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 2,                    "teacher_surname": 'Иванов',                    "teacher_name": 'Александр',                    "teacher_father_name": "Николаевич",                    "department": ('Кафедра Дифференциальных уравнений '                                   'и системного анализа'),                    "teacher_position": 'Старший преподаватель'                }            ]        }    }