from pydantic import BaseModel, Fieldclass Teacher(BaseModel):    id: int    teacher_surname: str = Field(max_length=50, default=None)    teacher_name: str = Field(max_length=50, default=None)    teacher_father_name: str = Field(max_length=50, default=None)    department: str = Field(max_length=200, default=None)    teacher_position: str = Field(max_length=150, default=None)    model_config = {        "json_schema_extra": {            "examples": [                {                    "id": 2,                    "teacher_surname": 'Иванов',                    "teacher_name": 'Александр',                    "teacher_father_name": "Николаевич",                    "department": ('Кафедра Дифференциальных уравнений '                                   'и системного анализа'),                    "teacher_position": 'Старший преподаватель'                }            ]        }    }